import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Cell(props) {
  return (
    <button onClick={() => props.onClick()}>{props.value}</button>
  );
}

function MainGrid(props) {
  return (
    <div class="grid">
      <Cell
        value={'qwe'}
        onClick={() => props.handleClick(0)}
      />
      <Cell
        value={'rtyu'}
        onClick={() => props.handleClick(1)}
      />
      <Cell
        value={'iop'}
        onClick={() => props.handleClick(2)}
      />
      <Cell
        value={'asd'}
        onClick={() => props.handleClick(3)}
      />
      <Cell
        value={'fgh'}
        onClick={() => props.handleClick(4)}
      />
      <Cell
        value={'jkl'}
        onClick={() => props.handleClick(5)}
      />
      <Cell
        value={'zxc'}
        onClick={() => props.handleClick(6)}
      />
      <Cell
        value={'vb'}
        onClick={() => props.handleClick(7)}
      />
      <Cell
        value={'nm'}
        onClick={() => props.handleClick(8)}
      />
    </div>
  );
}

function CharGroupGrid(props) {
  return (
    <div class="grid">
      <Cell
        value={'qwe'}
        onClick={() => props.handleClick(0)}
      />
    </div>
  );
}

class Keyboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: 'init',
      letter_group: null,
    };
  }

  handleMainGridClick(i) {
    this.setState({
      word: this.state.word,
      letter_group: i
    });
  }

  resetCharGroup() {
    this.setState({
      word: this.state.word,
      letter_group: null,
    });
  }

  clearWord() {
    this.setState({
      word: '',
      letter_group: null,
    })
  }

  delChar() {
    this.setState({
      word: this.state.word.length > 0 ? this.state.word.slice(0, -1) : '',
      letter_group: null,
    });
  }

  addSpace() {
    this.setState({
      word: this.state.word + ' ',
      letter_group: null,
    });
  }

  renderCharGroup() {

  }

  render() {
    if (this.state.letter_group != null) {
      return (
        <center>
            <div className="keyboard" onClick={() => this.resetCharGroup()}>
              {this.renderCharGroup()}
            </div>
        </center>
      );
    } else {
      return (
        <center>
            < br/><br />< br/>< br/>< br/><br />< br/>< br/>
            <div className="keyboard">
              <button
                id="top_buttons"
                onClick={() => this.clearWord()}
              >
                clear
              </button>
              <button
                id="top_buttons"
                onClick={() => this.delChar()}
              >
                del
              </button>
              <MainGrid handleClick={this.handleMainGridClick.bind(this)} />
              <button
                id="space_button"
                onClick={() => this.addSpace()}
              >
                space
              </button>
            </div>
            <br /><p id="current_word">{this.state.word}</p>
        </center>
      );
    }
  }
}

ReactDOM.render(
  <Keyboard />,
  document.getElementById('root')
);
