import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function CharacterGroup(props) {
  return (
    <button onClick={() => props.onClick()}>{props.value}</button>
  );
}

function MainGrid(props) {
  return (
    <div class="grid">
      <CharacterGroup
        value={'qwe'}
        onClick={() => props.handleClick(0)}
      />
      <CharacterGroup
        value={'rtyu'}
        onClick={() => props.handleClick(1)}
      />
      <CharacterGroup
        value={'iop'}
        onClick={() => props.handleClick(2)}
      />
      <CharacterGroup
        value={'asd'}
        onClick={() => props.handleClick(3)}
      />
      <CharacterGroup
        value={'fgh'}
        onClick={() => props.handleClick(4)}
      />
      <CharacterGroup
        value={'jkl'}
        onClick={() => props.handleClick(5)}
      />
      <CharacterGroup
        value={'zxc'}
        onClick={() => props.handleClick(6)}
      />
      <CharacterGroup
        value={'vb'}
        onClick={() => props.handleClick(7)}
      />
      <CharacterGroup
        value={'nm'}
        onClick={() => props.handleClick(8)}
      />
    </div>
  );
}

class Keyboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: '',
      letter_group: null,
    };
  }

  handleMainGridClick(i) {
    this.setState({
      word: this.state.word,
      letter_group: i
    });
  }

  resetCharGroup() {
    this.setState({
      word: this.state.word,
      letter_group: null,
    });
  }

  render() {

    if (this.state.letter_group != null) {
      return (
        <center>
            <div className="keyboard" onClick={() => this.resetCharGroup()}>
              Current Char Group: {this.state.letter_group}
            </div>
        </center>
      );
    } else {
      return (
        <center>
            <div className="keyboard" >
              Current Char Group: {this.state.letter_group}
              <MainGrid handleClick={this.handleMainGridClick.bind(this)} />
            </div>
        </center>
      );
    }
  }
}

ReactDOM.render(
  <Keyboard />,
  document.getElementById('root')
);
