import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function CharacterGroup(props) {
  return (
    <button onClick={() => props.onClick()}>{props.value}</button>
  );
}

function MainGrid(props) {
  return (
    <div class="grid">
      <CharacterGroup
        value={'qwe'}
        onClick={() => props.onClick(0)}
      />
      <CharacterGroup
        value={'rtyu'}
        onClick={() => props.onClick(1)}
      />
      <CharacterGroup
        value={'iop'}
        onClick={() => props.onClick(2)}
      />
      <CharacterGroup
        value={'asd'}
        onClick={() => props.onClick(3)}
      />
      <CharacterGroup
        value={'fgh'}
        onClick={() => props.onClick(4)}
      />
      <CharacterGroup
        value={'jkl'}
        onClick={() => props.onClick(5)}
      />
      <CharacterGroup
        value={'zxc'}
        onClick={() => props.onClick(6)}
      />
      <CharacterGroup
        value={'vb'}
        onClick={() => props.onClick(7)}
      />
      <CharacterGroup
        value={'nm'}
        onClick={() => props.onClick(8)}
      />
    </div>
  );
}

class Keyboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: '',
      letter_group: null,
    };
  }

  handleMainGridClick(i) {
    this.setState({
      word: this.state.word,
      letter_group: i
    });
  }

  render() {
    return (
      <center>
          <div className="keyboard" >
            Current Char Group: {this.state.letter_group}
            <MainGrid onClick={this.handleMainGridClick} />
          </div>
      </center>
    );
  }
}

ReactDOM.render(
  <Keyboard />,
  document.getElementById('root')
);
